import { React, useState, useEffect } from 'react';
import { ListGroupItem, ListGroup } from 'react-bootstrap';
import styles from './shopsdesign.module.css';
import API from '../../../api';
import { Card } from 'react-bootstrap';
import swal from 'sweetalert';
import Sidebar from '../sidebar';



function Shops() {


    const [shops, setShops] = useState([]);

    const getShops = async () => {
        try {
            await API.get(`shops`).then((res) => {
                const result = res.data;
                setShops(result);
            });
        } catch (error) {
            console.log(error);
        }
    };



    const deleteShop = async (id) => {
        const willDelete = await swal({
            title: "Delete",
            text: "Are you sure?",
            icon: "warning",
            dangerMode: true,
        });

        if (willDelete) {
            try {
                let res = await API.delete(`shops/${id}`);

                if (res.data.deletedCount === 1) {
                    await swal({ text: "deleted", icon: "success" });
                } else {
                    await swal("", res, "error");
                }
                window.location.reload();
            } catch (e) {
                console.log(e);
            }
        }
    };


    useEffect(() => {
        getShops();
    }, []);

    return (

        <>
            <div className={styles.shopswrapper}>

                <div>
                    <Sidebar />
                </div>

                <div className={styles.shopscard}>
                    {shops.map((sh) => {
                        return (
                            <>
                                <Card style={{ width: '10rem' , height:"2rem" }}>
                                    <img variant="top" src={`http://localhost:3001/uploads/${sh.shopimg}`}
                                    />
                                    <Card.Body>
                                        <Card.Title>{sh.shopname}</Card.Title>
                                        <Card.Text>
                                            {sh.locationInfo}.
                                        </Card.Text>
                                    </Card.Body>
                                    <ListGroup className="list-group-flush">
                                        <ListGroupItem>{sh.phonenumber}</ListGroupItem>
                                    </ListGroup>
                                    <Card.Body>

                                    </Card.Body>
                                </Card>
                            </>
                        )
                    })}

                </div>

            </div>

        </>
    )
}

export default Shops
