import { React, useState, useEffect } from 'react';
import { ListGroupItem } from 'react-bootstrap';
import { Card } from 'react-bootstrap';
import styles from './shopsdesign.module.css'
import Sidebar from '../sidebar';


const [shops, setShops] = useState([]);

const getShops = async () => {
    try {
        await API.get(`shops`).then((res) => {
            const result = res.data;
            setShops(result);
        });
    } catch (error) {
        console.log(error);
    }
};



const deleteShop = async (id) => {
    const willDelete = await swal({
        title: "Delete",
        text: "Are you sure?",
        icon: "warning",
        dangerMode: true,
    });

    if (willDelete) {
        try {
            let res = await API.delete(`shops/${id}`);

            if (res.data.deletedCount === 1) {
                await swal({ text: "deleted", icon: "success" });
            } else {
                await swal("", res, "error");
            }
            window.location.reload();
        } catch (e) {
            console.log(e);
        }
    }
};


useEffect(() => {
    getShops();
}, []);

function Shops() {
    return (

        <>
            <div classNam={styles.shopswrapper}>

                <Sidebar />

                <div className={styles.shopscard}>
                    {shops.map((sh) => {
                        return (
                            <>
                                <Card style={{ width: '18rem' }}>
                                    <Card.Img variant="top" src="holder.js/100px180?text=Image cap" />
                                    <Card.Body>
                                        <Card.Title>{sh.shopname}</Card.Title>
                                        <Card.Text>
                                            {sh.locationInfo}.
                                        </Card.Text>
                                    </Card.Body>
                                    <ListGroup className="list-group-flush">
                                        <ListGroupItem>{sh.phonenumber}</ListGroupItem>
                                        <ListGroupItem>Dapibus ac facilisis in</ListGroupItem>
                                        <ListGroupItem>Vestibulum at eros</ListGroupItem>
                                    </ListGroup>
                                    <Card.Body>

                                    </Card.Body>
                                </Card>
                            </>
                        )
                    })}

                </div>

            </div>

        </>
    )
}

export default Shops
