import { React } from 'react';
import { useState, useEffect } from 'react';
import { Card, Col, Row } from 'react-bootstrap';

import API from '../../api';
import styles from '../Dashboard/users/userdesign.module.css'


function Cardpart() {



    const [shops, setShops] = useState([]);

    const getShops = async () => {
        try {
            await API.get(`shops`).then((res) => {
                const result = res.data;
                setShops(result);
            });
        } catch (error) {
            console.log(error);
        }
    };


    //offer.slice(0, 5)

    useEffect(() => {
        getShops();
    }, []);

    return (



        <div className={styles.shopscard}>

            {shops.map((sh) => {
                return (
                    <>
                        <Row xs={1} md={2}  className="g-4">
                            {Array.from({ length: 1}).map((_, idx) => (
                                <Col>
                                    <Card>
                                        <Card.Img variant="top" src={`http://localhost:3001/uploads/${sh.shopimg}`} />
                                        <Card.Body>
                                            <Card.Title>{sh.shopname}</Card.Title>
                                            <Card.Text>
                                                {sh.locationinfon}
                                            </Card.Text>
                                            <Card.Text>
                                                {sh.phonenumber}
                                            </Card.Text>

                                        </Card.Body>
                                    </Card>
                                </Col>
                            ))}
                        </Row>
                    </>
                )
            })}
        </div>
    )
}

export default Cardpart
