import React, { useState, } from 'react';
import { useHistory } from 'react-router';
import CookieService from '../../CookieService';
import SessionContext from './SessionContext';
import swal from 'sweetalert';

export default function SessionProvider({ children }) {


    const [token, setToken] = useState('');
    const [role, setRole] = useState('');
    const [id, setId] = useState('');
    const history = useHistory();

    async function login({ username, password }) {


        try {
            let res = await fetch("http://localhost:3001/users/login", {
                method: "post",
                headers: {
                    'Content-type': 'application/json'
                },
                body: JSON.stringify({ username, password })

            });
            let result = await res.json();
            console.log("result is :", result);
            if (result.Token) {
                const options = { path: '/', };
                CookieService.set('Token', result.Token, options);
                CookieService.set('_id', result._id, options)

                setToken(result.Token);
                setRole(result.Role);
                setId(result._id);
            } else {
                swal("username or password is not correct ", " ", "warning");
            }
        } catch (e) {
            alert(e);
        }

    }

  async function logout(id) {

        try {
            let res = await fetch(`http://localhost:3001/users/logout/${id}`, {
                method: "post",
            });
            let result = await res.json();
            console.log("result is :", result);
        } catch (e) {
            alert(e);
        }

    }

    const context = {
        token,
        role,
        id,
        actions: {
            login,
            logout
        }
    }

    return (
        <SessionContext.Provider value={context}>
            {children}
        </SessionContext.Provider>
    )
}
