import { React, useState, useEffect } from 'react';
import { ListGroupItem, ListGroup } from 'react-bootstrap';
import styles from './shopsdesign.module.css';
import API from '../../../api';
import { Col,Card } from 'react-bootstrap';
import swal from 'sweetalert';
import Sidebar from '../sidebar';



function Shops() {


    const [shops, setShops] = useState([]);

    const getShops = async () => {
        try {
            await API.get(`shops`).then((res) => {
                const result = res.data;
                setShops(result);
            });
        } catch (error) {
            console.log(error);
        }
    };



    const deleteShop = async (id) => {
        const willDelete = await swal({
            title: "Delete",
            text: "Are you sure?",
            icon: "warning",
            dangerMode: true,
        });

        if (willDelete) {
            try {
                let res = await API.delete(`shops/${id}`);

                if (res.data.deletedCount === 1) {
                    await swal({ text: "deleted", icon: "success" });
                } else {
                    await swal("", res, "error");
                }
                window.location.reload();
            } catch (e) {
                console.log(e);
            }
        }
    };


    useEffect(() => {
        getShops();
    }, []);

    return (

        <>
            <div className={styles.shopswrapper}>

                <div>
                    <Sidebar />
                </div>

                <div className={styles.shopscard}>
                    {shops.map((sh) => {
                        return (
                            <>


                                <Row xs={1} md={2} className="g-4">
                                    {Array.from({ length: 4 }).map((_, idx) => (
                                        <Col>
                                            <Card>
                                                <Card.Img variant="top" src={`http://localhost:3001/uploads/${sh.shopimg}`} />
                                                <Card.Body>
                                                    <Card.Title>{sh.shopname}</Card.Title>
                                                    <Card.Text>
                                                        <p>  {sh.locationInfo} </p>
                                                        <p>  {sh.phonenumber} </p>

                                                    </Card.Text>
                                                </Card.Body>
                                            </Card>
                                        </Col>
                                    ))}
                                </Row>

                            </>
                        )
                    })}

                </div>

            </div>

        </>
    )
}

export default Shops
