import { React } from 'react';
import { useState, useEffect } from 'react';
import { Card, Col, Row } from 'react-bootstrap';
import API from '../../api';


function Cardpart() {



    const [shops, setShops] = useState([]);

    const getShops = async () => {
        try {
            await API.get(`shops`).then((res) => {
                const result = res.data;
                setShops(result);
            });
        } catch (error) {
            console.log(error);
        }
    };


    //offer.slice(0, 5)

    useEffect(() => {
        getShops();
    }, []);

    return (



        <>
            {shops.map((sh) => {
                return (
                    <>
                    <div className='container'>
                        <div class='row'>
                            <div class='col-lg-3 md-4'>
                                    <img src={`http://localhost:3001/uploads/${sh.shopimg}`}  style={{width:500}}/>
                                    <h3>{sh.shopname}</h3>
                                    <h4>
                                        {sh.locationinfon}
                                    </h4>
                                    <h4>
                                        {sh.phonenumber}
                                    </h4>
                                </div>
                            </div>
                            </div>



                    </>
                )

            })}




        </>

    )
}

export default Cardpart
