import React, { useEffect, useState } from "react";
import swal from 'sweetalert';
import { Card, ListGroupItem, ListGroup } from "react-bootstrap";
import styles from './usercard.module.css'
import Navbardash from "../navbardash";

import API from '../../../api';

function Users() {

    const [Users, setUsers] = useState([]);

    const getUsers = async () => {
        try {
            await API.get(`users`).then((res) => {
                const result = res.data;
                setUsers(result);
            });
        } catch (error) {
            console.log(error);
        }
    };



    const deleteUser = async (id) => {
        const willDelete = await swal({
            title: "Delete",
            text: "Are you sure?",
            icon: "warning",
            dangerMode: true,
        });

        if (willDelete) {
            try {
                let res = await API.delete(`users/${id}`);
                console.log(res.data);

                if (res.data.success) {
                    await swal("deleted");
                } else {
                    await swal("", res, "error");
                }
                window.location.reload();
            } catch (e) {
                console.log(e);
            }
        }
    };



    useEffect(() => {
        getUsers();
    }, []);

    return (
        <div style={{ display: "flex", flexDirection: "row", alignContent: "space-evenly" }}>
            <Navbardash />


            <div className="row">

                <div className={styles.usercardwrapper} >

                    {
                        Users.map((us) => (


                            <Card border="secondary" className={styles.parentcard}  className="col-3 col-md6 col"  >

                                <Card.Img variant="top" src={us.profilepic} />
                                <Card.Title> {us.username}  </Card.Title>
                                <ListGroup className="list-group-flush">
                                    <ListGroupItem>{us.mail}</ListGroupItem>
                                </ListGroup>

                            </Card>

                        ))
                    }

                </div>
            </div>
        </div>
    )
}

export default Users
