import React from 'react';
import API from '../../../api';
import Sidebar from '../sidebar';
import { useState, useEffect } from 'react';
import styles from './usercard.module.css';

function Edituser({ match }) {

    const [username, setUsername] = useState();
    const [password, setPassword] = useState();
    const [email, setEmail] = useState();
    const [profilepic, setProfilepic] = useState();

    var file;

    const onChangeFile = async (e) => {
        e.preventDefault(e);
        file = await e.target.files[0];
        setProfilepic(file);

    }


    const edituser = async (e) => {
        e.preventDefault(e);

        try {

            const body = new FormData();

            body.append('username', username);
            body.append('mail', email);
            body.append('password', password);
            body.append('profilepic', profilepic);


            await API.put(`users`, body, {
                headers: {
                    'Accept': 'multipart/form-data',
                },
            });

        } catch (e) {
            console.log(e);
        }
    }

    useEffect(() => {
        const getUser = async (e) => {
            try {
                const id = match.params.id;
                console.log(id);
                await API.get(`users/${id}`).then((res) => {
                    const result = res.data;
                    setUsername(result.username);
                    setEmail(result.mail);
                    setPassword(result.password);

                    console.log('hi', result);
                });
            } catch (e) {
                console.log(e);
            }
        }
        getUser();
    }, []);

    return (

        <div className={styles.adduserwrapper}>

            <Sidebar />

            <form onSubmit={edituser} className={styles.newuserform}>


                <label htmlFor="username" className={styles.adduserlabel}>
                    Username :
                    <input className="inputu" type="text" name="username" value={username} required onChange={(e) => { setUsername(e.target.value) }} />
                </label>


                <label htmlFor="email" className={styles.adduserlabel}>
                    Email :
                    <input className="inputu" type="email" name="email" value={email} required onChange={(e) => { setEmail(e.target.value) }} />
                </label>


                <label htmlFor="password" className={styles.adduserlabel}>
                    Password :
                    <input
                        className="inputu"
                        type="password"
                        name="password"
                        value={password}
                        required
                        onChange={(e) => { setPassword(e.target.value) }} />
                </label>

                <div>
                    <label htmlFor="pic" className={styles.chooseimg}>
                        choose image
                    </label>
                    <input className={styles.fileinput} type="file" id="pic" accept="image/*" multiple={false} onChange={onChangeFile} />
                </div>

                <div>
                    <input className={styles.submitbtn} type="submit" value="Add" />
                </div>

            </form>



        </div>

    )



}

export default Edituser;
