import React, { useState, } from 'react';
import { useHistory } from 'react-router';
import SessionContext from './SessionContext';
import swal from 'sweetalert';

export default function SessionProvider({ children }) {


    const [token, setToken] = useState('');
    const [role, setRole] = useState('');
    const [id, setId] = useState('');
    let history = useHistory();
    // useEffect(() => {

    //     function initializeSession() {
    //         let id = getCookie('id');
    //         let access_token = getCookie('access_token');
    //         if (access_token) fetch(`https://reqres.in/api/users/${id}`, {
    //             headers: {
    //                 'access_token': access_token
    //             }
    //         }).then(res => res.json()).then(res => {
    //             let user = { ...res.data, access_token };
    //             updateSession({ user });
    //         });
    //     }
    //     initializeSession();
    // }, []);

    // function updateSession(nextSession) {
    //     let value = typeof nextSession === "function" ?
    //         nextSession : prevSession => ({ ...prevSession, ...nextSession });
    //     setValue(value);
    // }

    async function login({ username, password }) {


        let body = new FormData();
        let username1 = username;
        let password1 = password;

        body.append('username', username1);
        body.append('password', password1);


        console.log("body",body);

        try {
            let res = await fetch("http://localhost:3001/users/login", {
                method: "post",
                body
            });
            let result = await res.json();
            console.log("result is :", result);

            if (result.Token) {
                setToken(result.Token);
                setRole(result.Role);
                setId(result._id)
                // CookieService.set("access_token", result.access_token, options);
                // CookieService.set("Role", result.data.role, options);
                // CookieService.set("UserName", result.data.username, options);
                history.push("/");
                console.log(result);
            } else {
                swal("username or password is not correct ", " ", "warning");
            }
        } catch (e) {
            alert(e);
        }

    }

    // function logout() {
    //     updateSession({ user: { access_token: null } });
    //     removeCookie('id');
    //     removeCookie('access_token');
    // }

    const context = {
        token,
        role,
        id,
        actions: {
            login,
            // logout
        }
    }

    return (
        <SessionContext.Provider value={context}>
            {children}
        </SessionContext.Provider>
    )
}
