import { React } from 'react';
import { useState, useEffect } from 'react';
import API from '../../api';
import cardpart from './cardpart.css';


function Cardpart() {

    const [shops, setShops] = useState([]);

    const getShops = async () => {
        try {
            await API.get(`shops`).then((res) => {
                const result = res.data;
                setShops(result);
            });
        } catch (error) {
            console.log(error);
        }
    };


    //offer.slice(0, 5)

    useEffect(() => {
        getShops();
    }, []);

    return (



        <>
            {shops.map((sh) => {
                return (
                    <>
                        <div className='container'>
                            <div class='row'>
                                <div class='col-lg-3 md-4'>
                                    <img src={`http://localhost:3001/uploads/${sh.shopimg}`} style={{ width: 500 }} />
                                    <h3>{sh.shopname}</h3>
                                    <h4>
                                        {sh.locationinfon}
                                    </h4>
                                    <h4>
                                        {sh.phonenumber}
                                    </h4>
                                </div>
                            </div>
                        </div>



                    </>
                )

            })}


            <div className="content">
                <div className="container">
                    <div className="row">
                        <div className="col-xs-12 col-sm-4">
                            <div className="card">
                                <a className="img-card" href="http://www.fostrap.com/2016/03/bootstrap-3-carousel-fade-effect.html">
                                    <img src="https://1.bp.blogspot.com/-Bii3S69BdjQ/VtdOpIi4aoI/AAAAAAAABlk/F0z23Yr59f0/s640/cover.jpg" />
                                </a>
                                <div className="card-content">
                                    <h4 className="card-title">
                                        <a href="http://www.fostrap.com/2016/03/bootstrap-3-carousel-fade-effect.html"> Bootstrap 3 Carousel FadeIn Out Effect
                                        </a>
                                    </h4>
                                    <p className="">
                                        Tutorial to make a carousel bootstrap by adding more wonderful effect fadein ...
                                    </p>
                                </div>
                                <div className="card-read-more">
                                    <a href="http://www.fostrap.com/2016/03/bootstrap-3-carousel-fade-effect.html" class="btn btn-link btn-block">
                                        Read More
                                    </a>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

        </>

    )
}

export default Cardpart
