import { React } from 'react';
import { useState, useEffect } from 'react';
import { Card, Col } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { ListGroup } from 'react-bootstrap';
import { ListGroupItem } from 'react-bootstrap';
import API from '../../api';
import styles from '../Dashboard/users/userdesign.module.css'


function Cardpart() {



    const [shops, setShops] = useState([]);

    const getShops = async () => {
        try {
            await API.get(`shops`).then((res) => {
                const result = res.data;
                setShops(result);
            });
        } catch (error) {
            console.log(error);
        }
    };


    //offer.slice(0, 5)

    useEffect(() => {
        getShops();
    }, []);

    return (



        <div className={styles.shopscard}>

            {shops.map((sh) => {
                return (
                    <div className="col-lg-4 col-md-4">
					<div className="fh5co-blog animate-box">
						<a href="#"><img className="img-responsive" src="images/gallery-4.jpg" alt=""/></a>
						<div className="blog-text">
							<h3><a href="">sh.shopname</a></h3>
							<span className="posted_on">Sep. 15th</span>
							<span className="comment"><a href="">21<i className="icon-speech-bubble"></i></a></span>
							<p>Far far away, behind the word mountains, far from the countries Vokalia and Consonantia, there live the blind texts.</p>
							<a href="#" className="btn btn-primary">Read More</a>
						</div> 
					</div>
				</div>

                )
            })}
        </div>
    )
}

export default Cardpart
