            let res = await fetch("http://localhost:3001/login", {
                method: "post",
                body,
            });
            let result = await res.json();
            console.log("result is :", result);

            if (result.Token) {
                const options = { path: "/" };
                setToken(result.Token);
                setRole(result.Role);
                setId(result._id)
                // CookieService.set("access_token", result.access_token, options);
                CookieService.set("Role", result.data.role, options);
                CookieService.set("UserName", result.data.username, options);
                history.push("/HomePage");
                console.log(result);
            } else {
                swal("username or password is not correct ", " ", "warning");
            }
