import { React } from 'react';
import { useState, useEffect } from 'react';
import { Card, Col } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { ListGroup } from 'react-bootstrap';
import { ListGroupItem } from 'react-bootstrap';
import API from '../../api';
import styles from '../Dashboard/users/userdesign.module.css'


function Cardpart() {



    const [shops, setShops] = useState([]);

    const getShops = async () => {
        try {
            await API.get(`shops`).then((res) => {
                const result = res.data;
                setShops(result);
            });
        } catch (error) {
            console.log(error);
        }
    };


    //offer.slice(0, 5)

    useEffect(() => {
        getShops();
    }, []);

    return (


        <div className={styles.shopswrapper}>

        <div className={styles.shopscard}>

            {shops.map((sh) => {
                return (
                    <>
                        <Col style={{ marginBottom: "2rem" }} key={sh._id} >
                            <div className={styles.bordercard} >
                                <div className={styles.imgwrapper}>

                                    <Link className="linksite" to={`shops/edit/${sh._id}`}>
                                        <img
                                            className={styles.card_shopimg}
                                            src={`http://localhost:3001/uploads/${sh.shopimg}`}
                                            alt="logo img"
                                            height={150}
                                            width={250}
                                        />
                                    </Link>
                                </div>
                                <div classname={styles.cardbody}>

                                    <h6 className={styles.cardt} >
                                        {sh.shopname}
                                    </h6>
                                    <Card.Text>

                                        <ListGroup className="list-group-flush">
                                            <ListGroupItem>  {sh.locationInfo} </ListGroupItem>
                                            <ListGroupItem className={styles.phoneshops}>  {sh.phonenumber} </ListGroupItem>
                                            {sh.accept !== "no" ? (

                                                <ListGroupItem> <i class="fas fa-check-circle fa-2x"></i>  </ListGroupItem>
                                            ) : (
                                                <ListGroupItem>  <i class="far fa-times-circle fa-2x"></i> </ListGroupItem>

                                            )}
                                        </ListGroup>

                                    </Card.Text>
                                </div>
                            </div>
                        </Col>

                    </>
                )
            })}
        </div>
        </div>
    )
}

export default Cardpart
