import React from 'react'
import { useState ,useEffect } from 'react';
import API from '../../api';
// shopsp go for shops page //

function Shopsp() {

    const [region, setRegion] = useState('nothing');
    const [cities, setCities] = useState([]);
    const [locationInfo, setLocationInfo] = useState();

    const leb = State.getStatesOfCountry('LB');


    const [shops, setShops] = useState([]);

    const getShops = async () => {

        const body = new FormData();
        body.append('locationInfo', locationInfo);

        try {
            await API.get(`shops/get/location`, body).then((res) => {
                const result = res.data;
                setShops(result);
            });
        } catch (error) {
            console.log(error);
        }
    };



    const dealwithregion = async (code) => {

        setRegion("filled");

        setCities(City.getCitiesOfState('LB', code.toString()));

    }


    useEffect(() => {
        getShops();
    }, []);


    return (

        <div>

            <label htmlFor="regionInfo" >
                Select Region :
                <br />
                <select
                    required
                    name="regionInfo"
                    className="inputu"
                    onChange={e => dealwithregion(e.target.value)}
                >
                    <option value="">None</option>

                    {leb.map((lb) => {
                        return (
                            <option
                                value={lb.isoCode}
                                key={lb.name}
                            >{lb.name}
                            </option>
                        )
                    })}
                </select>

            </label>


            {region !== "nothing" ? (

                <div>

                    <label htmlFor="regionInfo" >

                        Select City:
                        <br />
                        <select
                            required
                            name="locationInfo"
                            className="inputu"
                            onChange={e => setLocationInfo(e.target.value)}
                        >
                            <option value="">None</option>

                            {cities.map((ct) => {
                                return (
                                    <option
                                        value={ct.name}
                                        key={ct.name}
                                    >{ct.name}
                                    </option>
                                )
                            })}

                        </select>

                    </label>

                    <p>

                    </p>

                </div>
            ) : (
                <p> </p>
            )}

            {shops ? (

                <div className="content">
                    <div className="container">
                        <div className="row">
                            {shops.slice(0, 3).map((sh) => {
                                return (
                                    <div className="col-xs-12 col-sm-4">
                                        <div className="card">
                                            <a className="img-card" href="http://www.fostrap.com/2016/03/bootstrap-3-carousel-fade-effect.html">
                                                <img src={`http://localhost:3001/uploads/${sh.shopimg}`} alt="database img" />
                                            </a>
                                            <div className="card-content">
                                                <h4 className="card-title">
                                                    <a href="http://www.fostrap.com/2016/03/bootstrap-3-carousel-fade-effect.html"> {sh.shopname} </a>
                                                </h4>
                                                <p className="">
                                                    {sh.phonenumber}
                                                </p>
                                            </div>
                                            <div className="card-read-more">
                                                <a href="http://www.fostrap.com/2016/03/bootstrap-3-carousel-fade-effect.html" class="btn btn-link btn-block">
                                                    Contact Shop
                                                </a>
                                            </div>
                                        </div>
                                    </div>
                                )
                            })}
                        </div>
                    </div>
                </div>


            ) : (
                <p> </p>
            )}

        </div>
    )
}

export default Shopsp
