import React, { useEffect, useState } from "react";
import swal from 'sweetalert';
import Userscard from './userscard';
import Navbardash from "../navbardash";
import { Table, Thead, Tbody, Tr, Th } from 'react-super-responsive-table';
import 'react-super-responsive-table/dist/SuperResponsiveTableStyle.css';

import API from '../../../api';

function Users() {

    const [Users, setUsers] = useState([]);

    const getUsers = async () => {
        try {
            await API.get(`users`).then((res) => {
                const result = res.data;
                setUsers(result);
            });
        } catch (error) {
            console.log(e);

        }
    };



    const deleteUser = async (id) => {
        const willDelete = await swal({
          title: "Delete",
          text: "Are you sure",
          icon: "warning",
          dangerMode: true,
        });

        if (willDelete) {
          try {
            let res = await API.delete(`deleteSubsbystudentId/${id}`);
            let res1 = await API.delete(`deletestudent/${id}`);
            const result = res1.data.message;
            if (res1.data.success && res.data.success) {
              await swal("تم خذفه", result, "success");
            } else {
              await swal("", result, "error");
            }
            window.location.reload();
          } catch (e) {
            console.log(e);
          }
        }
      };



    useEffect(() => {
        getUsers();
    }, []);

    return (
        <div style={{ display:"flex" , }}>
            <Navbardash />

            <Table>
                <Thead>
                    <Tr>
                        <Th>username</Th>
                        <Th>email</Th>
                        <Th>profilepic</Th>
                    </Tr>
                </Thead>
                <Tbody>
                    {
                        Users.map((us) => (
                            <Userscard
                                key={us._id}
                                Userid={us._id}
                                Username={us.username}
                                Email={us.mail}
                                Profilepic={us.profilepic}
                                  delete={deleteUser}
                                status={us.Status}
                            />
                        ))
                    }
                </Tbody>

            </Table>
        </div>
    )
}

export default Users
