import React, { useEffect, useState } from "react";
import { Card, ListGroupItem, ListGroup } from "react-bootstrap";
imp
import swal from 'sweetalert';
import styles from './usercard.module.css'
import Navbardash from "../navbardash";

import API from '../../../api';

function Users() {

    const [Users, setUsers] = useState([]);

    const getUsers = async () => {
        try {
            await API.get(`users`).then((res) => {
                const result = res.data;
                setUsers(result);
            });
        } catch (error) {
            console.log(error);
        }
    };



    const deleteUser = async (id) => {
        const willDelete = await swal({
            title: "Delete",
            text: "Are you sure?",
            icon: "warning",
            dangerMode: true,
        });

        if (willDelete) {
            try {
                let res = await API.delete(`users/${id}`);
                console.log(res.data);

                if (res.data.success) {
                    await swal("deleted");
                } else {
                    await swal("", res, "error");
                }
                window.location.reload();
            } catch (e) {
                console.log(e);
            }
        }
    };



    useEffect(() => {
        getUsers();
    }, []);

    return (
        <div className={styles.userswrapper}>

            <div className="navinsiduser">
                <Navbardash />
            </div>

            <div className={styles.card}>

                <div className={styles.usercardwrapper} >

                    {
                        Users.map((us) => (


                            <Card border="secondary" className={styles.parentcard} >
                                <Card.Img variant="top" className={styles.imgcard} src={us.profilepic} />
                                <Card.Title> {us.username}  </Card.Title>
                                <ListGroup className="list-group-flush">
                                    <ListGroupItem>{us.mail}</ListGroupItem>
                                </ListGroup>
                            </Card>

                        ))
                    }



{Users.map((us) => {
          return (
            <div className="card" key={us.id} style={{ position: 'relative',overflow:"hidden" }} >
              <Link
                style={{ backgroundColor: '#F27F1C', color: '#fff', padding: '4.5px',paddingLeft:'8.5px' ,borderTop: '0', position: 'absolute', top: '0', right: '0', borderTopRightRadius:'40% 28%', cursor: 'pointer' }}
                className="link"
                to={`/admins/${us.id}`}>
                <i class="fas fa-pencil-alt"></i>
              </Link>

              <div className="card_img">
                <img
                  className="card_emp"
                  src={avatar}
                  alt="logo img"
                  height={150}
                  width={250}
                />
              </div>
              <h3>{us.firstname}</h3>
              <p className="text_card">L : {us.lastname}</p>
              <p className="text_card">E : {us.email}</p>

            </div>
          );
        })}
                </div>
            </div>
        </div>
    )
}

export default Users
